					and ur.role_id = #{roleId}

				   and a.gzh_openid = #{gzhOpenid} 
				   and a.name <> '' 
				   and a.user_type = #{userType} 



                order by ${sort} ${order}


                order by a.user_id desc



			limit #{offset}, #{limit}

